import { useD3 } from '../../hooks/useD3';
import React from 'react';
import * as d3 from 'd3';

function StickyForceLayout({ data, sizeMax, degree, view, metric }) {
  const width = 800;
  const height = 800;
  const ref = useD3(
    (svg) => {
      (svg = d3.create('svg').attr('viewBox', [0, 0, width, height])),
        (link = svg
          .selectAll('.link')
          .data(graph.links)
          .join('line')
          .classed('link', true)),
        (node = svg
          .selectAll('.node')
          .data(graph.nodes)
          .join('circle')
          .attr('r', 12)
          .classed('node', true)
          .classed('fixed', (d) => d.fx !== undefined));

      const simulation = d3
        .forceSimulation()
        .nodes(graph.nodes)
        .force('charge', d3.forceManyBody())
        .force('center', d3.forceCenter(width / 2, height / 2))
        .force('link', d3.forceLink(graph.links))
        .on('tick', tick);

      const drag = d3.drag().on('start', dragstart).on('drag', dragged);

      node.call(drag).on('click', click);

      function tick() {
        link
          .attr('x1', (d) => d.source.x)
          .attr('y1', (d) => d.source.y)
          .attr('x2', (d) => d.target.x)
          .attr('y2', (d) => d.target.y);
        node.attr('cx', (d) => d.x).attr('cy', (d) => d.y);
      }

      function click(event, d) {
        delete d.fx;
        delete d.fy;
        d3.select(this).classed('fixed', false);
        simulation.alpha(1).restart();
      }

      function dragstart() {
        d3.select(this).classed('fixed', true);
      }

      function dragged(event, d) {
        d.fx = clamp(event.x, 0, width);
        d.fy = clamp(event.y, 0, height);
        simulation.alpha(1).restart();
      }
    },
    [data]
  );

  if (view === 'bar') return null;
  return (
    <div id='content'>
      <div
        className='tooltip'
        style={{ position: 'absolute', background: 'lightgray' }}
      ></div>
      <svg
        width={width}
        height={height}
        onMouseEnter={forceUpdate}
        onMouseLeave={forceUpdate}
      >
        <g transform='translate(50, 200)'></g>
      </svg>
    </div>
  );
}

export default StickyForceLayout;
