import React from 'react';
import Drawer from '@mui/material/Drawer';
import Button from '@mui/material/Button';
import Slider from '@mui/material/Slider';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Select from '@mui/material/Select';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import FormHelperText from '@mui/material/FormHelperText';
import Divider from '@mui/material/Divider';
import './LeftDrawer.css';

const LeftDrawer = () => {
  const [metric, setMetric] = React.useState('accuracy');

  const handleMetricChange = (event) => {
    setMetric(event.target.value);
  };

  return (
    <Drawer
      variant='permanent'
      sx={{
        display: { xs: 'none', sm: 'block' },
        '& .MuiDrawer-paper': { boxSizing: 'border-box', width: '15rem' },
      }}
      open
    >
      <div className='container'>
        <h1>Slice Filters:</h1>
        <h2>Number of Features:</h2>
        <Slider
          aria-label='Number of Features'
          defaultValue={2}
          valueLabelDisplay='auto'
          step={1}
          marks
          min={1}
          max={5}
          size='small'
        />
        <h2>Minimum Sample Size:</h2>
        <Slider
          size='small'
          defaultValue={1000}
          aria-label='Small'
          valueLabelDisplay='auto'
          min={0}
          max={10000}
          step={100}
        />
        <h2>Fairness Metric:</h2>
        <FormControl sx={{ m: 1, minWidth: 175 }}>
          <InputLabel id='demo-simple-select-helper-label'>Metric</InputLabel>
          <Select
            labelId='demo-simple-select-helper-label'
            id='demo-simple-select-helper'
            value={metric}
            label='Metric'
            onChange={handleMetricChange}
          >
            <MenuItem value={'accuracy'}>Accuracy</MenuItem>
            <MenuItem value={'precision'}>Precision</MenuItem>
            <MenuItem value={'recall'}>Recall</MenuItem>
            <MenuItem value={'specificity'}>Specificity</MenuItem>
            <MenuItem value={'f1'}>F1</MenuItem>
            <MenuItem value={'false negative rate'}>
              False Negative Rate
            </MenuItem>
            <MenuItem value={'false positive rate'}>
              False Positive Rate
            </MenuItem>
          </Select>
        </FormControl>
        {/* <FormGroup style={{ paddingLeft: '1rem' }}>
          <FormControlLabel control={<Checkbox />} label='Accuracy' />
          <FormControlLabel control={<Checkbox />} label='Precision' />
          <FormControlLabel control={<Checkbox />} label='Recall' />
          <FormControlLabel control={<Checkbox />} label='Specificity' />
          <FormControlLabel control={<Checkbox />} label='F1' />
          <FormControlLabel
            control={<Checkbox />}
            label='False Negative Rate'
          />
          <FormControlLabel
            control={<Checkbox />}
            label='False Positive Rate'
          />
        </FormGroup> */}
        <Divider style={{ padding: '1rem' }} />
        <Button variant='outlined' style={{ marginTop: '1rem' }}>
          Reset
        </Button>
      </div>
    </Drawer>
  );
};

export default LeftDrawer;
