import React from 'react';
import Drawer from '@mui/material/Drawer';
import Button from '@mui/material/Button';
import Slider from '@mui/material/Slider';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Select from '@mui/material/Select';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Divider from '@mui/material/Divider';
import Switch from '@mui/material/Switch';
import './LeftDrawer.css';

const LeftDrawer = ({
  numFeatures,
  setNumFeatures,
  sampleSize,
  setSampleSize,
  metric,
  setMetric,
  sortBy,
  setSortBy,
  overperforming,
  setOverperforming,
  features,
  setFeatures,
  view,
  radius,
  setRadius,
  edgeFiltering,
  setEdgeFiltering,
  edgeForce,
  setEdgeForce,
  cursorMode,
  setCursorMode,
  showConvexHull,
  setShowConvexHull,
}) => {
  const [switchDisabled, setSwitchDisabled] = React.useState(false);
  const handleFeaturesChange = (event) => {
    setNumFeatures(event.target.value);
    setShowConvexHull(false);
  };

  const handleSizeChange = (event) => {
    setSampleSize(event.target.value);
    setShowConvexHull(false);
  };

  const handleMetricChange = (event) => {
    setMetric(event.target.value);
    setShowConvexHull(false);
  };

  const handleSortByChange = (event) => {
    setSortBy(event.target.value);
    setShowConvexHull(false);
  };

  const handleSwitchChange = (event) => {
    setOverperforming(event.target.checked);
    setShowConvexHull(false);
  };

  const handleCheckboxChange = (event, label) => {
    if (event.target.checked) {
      setFeatures([...features, label]);
    } else {
      setFeatures(features.filter((feature) => feature !== label));
    }
    setShowConvexHull(false);
  };

  const handleReset = (event) => {
    setNumFeatures(2);
    setSampleSize(0);
    setMetric('Log Loss');
    setSortBy('metric');
    setOverperforming(false);
    setEdgeFiltering(300);
    setEdgeForce(1);
    setShowConvexHull(false);
  };

  const [open, setOpen] = React.useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  return (
    <Drawer
      variant="persistent"
      anchor="left"
      sx={{
        display: { xs: 'none', sm: 'block' },
        '& .MuiDrawer-paper': { boxSizing: 'border-box', width: '15rem' },
      }}
      open
    >
      <div className='left-container'>
        <h1>Slice Filters</h1>
        <h2>Number of Features:</h2>
        <Slider
          aria-label='Number of Features'
          defaultValue={2}
          value={numFeatures}
          valueLabelDisplay='auto'
          step={1}
          marks
          min={1}
          max={4}
          size='small'
          onChange={handleFeaturesChange}
        />
        <h2>Minimum Slice Size:</h2>
        <Slider
          size='small'
          defaultValue={100}
          aria-label='Small'
          value={sampleSize}
          valueLabelDisplay='auto'
          min={0}
          max={250}
          step={10}
          onChange={handleSizeChange}
        />
        {view === 'graph' && (
          <>
            <h2>Edge Filtering:</h2>
            <Slider
              aria-label='Edge Filtering'
              defaultValue={300}
              value={edgeFiltering}
              valueLabelDisplay='auto'
              step={10}
              min={0}
              max={1000}
              size='small'
              onChange={(event) => {
                setEdgeFiltering(event.target.value);
                setShowConvexHull(false);
              }}
            />
            <h2>Edge Force Strength:</h2>
            <Slider
              aria-label='Edge Force Strength'
              defaultValue={1}
              value={edgeForce}
              valueLabelDisplay='auto'
              step={0.01}
              min={0}
              max={5}
              size='small'
              onChange={(event) => {
                setEdgeForce(event.target.value);
                setShowConvexHull(false);
              }}
            />
            <h2>Cursor Mode:</h2>
            <FormControl sx={{ s: 1, minWidth: 175 }}>
              <InputLabel>Mode:</InputLabel>
              <Select
                value={cursorMode}
                label='Mode'
                onChange={(event) => {
                  setCursorMode(event.target.value);
                  setShowConvexHull(false);
                }}
              >
                <MenuItem value={'drag'}>Drag</MenuItem>
                <MenuItem value={'select'}>Select</MenuItem>
              </Select>
            </FormControl>
          </>
        )}
        {/* <h2>Fairness Metric:</h2>
        <FormControl sx={{ m: 1, minWidth: 175 }}>
          <InputLabel id='demo-simple-select-helper-label'>Metric</InputLabel>
