import { useD3 } from '../../hooks/useD3';
import React from 'react';
import * as d3 from 'd3';

function ForceLayout({ data, model, max, sizeMax }) {
  console.log(data.length);
  const width = 700;
  const height = 900;
  const ref = useD3(
    (svg) => {
      const colorScale = ['orange', 'lightblue', '#B19CD9'];
      const features = [];
      data.forEach((obj) => {
        obj.classifiers = [];
        let str = obj.slice;
        while (str.indexOf(':') !== -1) {
          let temp = str.substring(0, str.indexOf(':'));
          if (!features.includes(temp)) features.push(temp);
          if (temp.indexOf(',') !== -1) {
            str = temp.substring(',' + 2);
          } else {
            str = '';
          }
        }
      });
      console.log(features);
      const xCenter = [];
      const yCenter = [];
      for (let i = 0; i < features.length; i++) {
        xCenter.push((width / features.length) * i + 100);
        yCenter.push((height / features.length) * i + 100);
      }
      const numNodes = data.length;
      const nodes = data.map((obj) => {
        return {
          radius: Math.max((obj.size / sizeMax) * 80, 10),
          category: obj.degree,
        };
      });

      const simulation = d3
        .forceSimulation(nodes)
        .force('charge', d3.forceManyBody().strength(5))
        .force(
          'x',
          d3.forceX().x(function (d) {
            return xCenter[d.category];
          })
        )
        .force(
          'y',
          d3.forceY().y(function (d) {
            return yCenter[d.category];
          })
        )
        .force(
          'collision',
          d3.forceCollide().radius(function (d) {
            return d.radius;
          })
        )
        .on('tick', ticked);

      function ticked() {
        var u = d3
          .select('svg g')
          .selectAll('circle')
          .data(nodes)
          .join('circle')
          .attr('r', function (d) {
            return d.radius;
          })
          .style('fill', function (d) {
            return colorScale[d.category];
          })
          .attr('cx', function (d) {
            return d.x;
          })
          .attr('cy', function (d) {
            return d.y;
          });
      }
    },
    [data.length]
  );

  return (
    <div id='content'>
      <svg width={width} height={height}>
        <g transform='translate(50, 200)'></g>
      </svg>
    </div>
  );
}

export default ForceLayout;
