import { useD3 } from '../../hooks/useD3';
import React from 'react';
import * as d3 from 'd3';

function ForceLayout({ data, model, max, sizeMax, degree }) {
  const [hover, setHover] = React.useState(false);
  const width = 800;
  const height = 800;
  const ref = useD3(
    (svg) => {
      let colorScale = ['orange', 'lightblue', '#B19CD9'];
      let originalColorScale = ['orange', 'lightblue', '#B19CD9'];
      let hoverColorScale = ['orange', 'orange', 'orange'];
      const features = [];
      data.forEach((obj) => {
        obj.classifiers = [];
        let str = obj.slice;
        while (str.indexOf(':') !== -1) {
          let temp = str.substring(0, str.indexOf(':'));
          obj.classifiers.push(temp);
          if (!features.includes(temp)) features.push(temp);
          if (str.indexOf(',') !== -1) {
            str = str.substring(str.indexOf(',') + 2);
            console.log(str);
          } else {
            str = '';
          }
        }
      });
      console.log(data);
      const xCenter = [];
      const yCenter = [];
      for (let i = 0; i < features.length; i++) {
        xCenter.push(((width - 200) / features.length) * i + 100);
        yCenter.push(((height - 200) / features.length) * i - 100);
      }
      const numNodes = data.length;
      const nodes = data.map((obj) => {
        return {
          radius: Math.max((obj.size / sizeMax) * 80, 10),
          category: obj.degree,
          xFeature: obj.classifiers[0],
          yFeature: obj.classifiers[1] ?? obj.classifiers[0],
        };
      });
      console.log(nodes);

      const simulation = d3
        .forceSimulation(nodes)
        .force('charge', d3.forceManyBody().strength(5))
        .force(
          'x',
          d3.forceX().x(function (d) {
            return xCenter[features.indexOf(d.xFeature)];
          })
        )
        .force(
          'y',
          d3.forceY().y(function (d) {
            if (degree > 1) {
              return yCenter[features.indexOf(d.yFeature)];
            } else {
              return height / 4;
            }
          })
        )
        .force(
          'collision',
          d3.forceCollide().radius(function (d) {
            return d.radius;
          })
        )
        .on('tick', ticked);

      function ticked() {
        var u = d3
          .select('svg g')
          .selectAll('circle')
          .data(nodes)
          .join('circle')
          .attr('r', function (d) {
            return d.radius;
          })
          .attr('cx', function (d) {
            return d.x;
          })
          .attr('cy', function (d) {
            return d.y;
          })
          .on('mouseover', function (d) {
            setHover(true);
            colorScale = hoverColorScale;
            // d3.select(this).attr('fill', 'black');
            console.log(hover);
          })
          .on('mouseout', function (d) {
            setHover(false);
            colorScale = colorScale;
            console.log(hover);
          })
          .style('fill', function (d) {
            if (hover) {
              return hoverColorScale[d.category];
            }
            return colorScale[d.category];
          });
      }
    },
    [data.length]
  );

  return (
    <div id='content'>
      <svg width={width} height={height}>
        <g transform='translate(50, 200)'></g>
      </svg>
    </div>
  );
}

export default ForceLayout;
