import { useD3 } from '../../hooks/useD3';
import React from 'react';
import * as d3 from 'd3';

function ForceLayout({ data, model, max, sizeMax }) {
  console.log(data.length);
  const width = 600;
  const height = 700;
  const ref = useD3(
    (svg) => {
      const colorScale = ['orange', 'lightblue', '#B19CD9'];
      const xCenter = [100, 300, 500];

      const numNodes = data.length;
      const nodes = data.map((obj) => {
        return {
          radius: Math.max(((obj.size - 10) / (sizeMax - 10)) * 100, 10),
          category: obj.degree,
        };
      });

      const simulation = d3
        .forceSimulation(nodes)
        .force('charge', d3.forceManyBody().strength(5))
        .force(
          'x',
          d3.forceX().x(function (d) {
            return xCenter[d.category];
          })
        )
        .force(
          'collision',
          d3.forceCollide().radius(function (d) {
            return d.radius;
          })
        )
        .on('tick', ticked);

      function ticked() {
        var u = d3
          .select('svg g')
          .selectAll('circle')
          .data(nodes)
          .join('circle')
          .attr('r', function (d) {
            return d.radius;
          })
          .style('fill', function (d) {
            return colorScale[d.category];
          })
          .attr('cx', function (d) {
            return d.x;
          })
          .attr('cy', function (d) {
            return d.y;
          });
      }
    },
    [data.length]
  );

  return (
    <div id='content'>
      <svg width={width} height={height}>
        <g transform='translate(50, 200)'></g>
      </svg>
    </div>
  );
}

export default ForceLayout;
